//sample component
POXA.registerComponent('rot', {
	schema: {
		speed:{type:"number",default:60},
		axis:{default:"Y"}
	},
	init:function() {
	  this.rot = {x:0,y:0,z:0}
	},
	tick:function(time, timeDelta) {
		if(this.data.axis=="X") this.rot.x = THREE.Math.degToRad(time/this.data.speed)
		if(this.data.axis=="Y") this.rot.y = THREE.Math.degToRad(time/this.data.speed)
		if(this.data.axis=="Z") this.rot.z = THREE.Math.degToRad(time/this.data.speed)
//		this.el.setAttribute("rotation",this.rot)
		this.el.object3D.rotation.set(this.rot.x,this.rot.y,this.rot.z)
	}
})
